import React, { useState, useEffect } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import axios from "axios";

import PortfolioSummary from "./components/PortfolioSummary";
import StockList from "./components/StockList";
import StockDetails from "./components/StockDetails";
import AddStock from "./components/AddStock";

function App() {
  const [stocks, setStocks] = useState([]);
  const [selectedStock, setSelectedStock] = useState(null);
  const [refresh, setRefresh] = useState(false); // Used to trigger refreshing of stock list

  // Placeholder username - replace with dynamic username after implementing login
  const username = "exampleUser";

  useEffect(() => {
    // Fetch the list of stocks for the user when the app loads or refresh is triggered
    axios
      .get(`${process.env.REACT_APP_API_URL}/portfolio/${username}`) // Adjust API URL as needed
      .then((response) => {
        setStocks(response.data);
        setRefresh(false); // Reset refresh state after fetching
      })
      .catch((error) => console.error("Failed to fetch stocks", error));
  }, [refresh]); // Depend on refresh state to re-trigger fetching

  // Function to handle stock selection from the list
  const handleStockSelect = (ticker) => {
    setSelectedStock(ticker);
  };

  // Function to handle refreshing the stock list, e.g., after adding a stock
  const handleRefresh = () => {
    setRefresh(true);
  };

  return (
    <Container fluid>
      <Row className="justify-content-md-center">
        <h1>My Portfolio</h1>
      </Row>
      <Row>
        <Col>
          <PortfolioSummary username={username} />
        </Col>
      </Row>
      <Row>
        <Col xs={4}>
          <StockList stocks={stocks} onSelect={handleStockSelect} />
          <AddStock username={theActualUsername} onStockAdded={handleRefresh} />
        </Col>
        <Col xs={8}>
          {selectedStock ? (
            <StockDetails
              ticker={selectedStock}
              onStockUpdated={handleRefresh}
              username={username}
            />
          ) : (
            <div>Select a stock to view details.</div>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
