// Login.js
import React, { useState } from "react";
import axios from "axios";
import { Form, Button, Alert } from "react-bootstrap";

const Login = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/login`,
        {
          username,
          password,
        }
      );

      // Assuming the token is returned in response.data.access_token
      if (response.data.access_token) {
        localStorage.setItem("token", response.data.access_token); // Store token
        onLoginSuccess(response.data.access_token); // Notify App.js about the login success
      } else {
        setErrorMessage("Login failed. Please check your credentials.");
      }
    } catch (error) {
      setErrorMessage(
        error.response.data.error || "An error occurred during login."
      );
    }
  };

  return (
    <div>
      <Form onSubmit={handleSubmit}>{/* Form fields */}</Form>
    </div>
  );
};

export default Login;
