import React, { useState } from "react";
import { Button } from "@nextui-org/react";
import { Link, useNavigate } from "react-router-dom";
import { useUser } from "./UserContext"; // Ensure this hook is correctly implemented

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const { setUserData } = useUser(); // This should update context and potentially persist to localStorage

  const handleSubmit = async (e) => {
    e.preventDefault();

    const loginDetails = { username, password };

    try {
      const response = await fetch("http://127.0.0.1:5000/login", {
        // Adjusted to a relative URL, ensure this matches your backend route
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(loginDetails),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to login");
      }

      const data = await response.json();
      console.log("Login successful", data);

      // Assuming data includes a user object with username and an access token. Adjust according to your actual response.
      setUserData({ username: data.username, accessToken: data.access_token });

      // Optionally, persist user data for session management
      localStorage.setItem(
        "userData",
        JSON.stringify({
          username: data.username,
          accessToken: data.access_token,
        })
      );

      navigate("/summary"); // Redirect to the summary page or wherever appropriate
    } catch (error) {
      console.error("Login error:", error.message);
      setError(error.message);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "60vh",
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "20px",
          width: "300px",
          textAlign: "center",
        }}
      >
        <h2 style={{ fontSize: "24px", marginBottom: "20px" }}>
          Log In to Your Account
        </h2>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
          style={{ padding: "10px", fontSize: "16px" }}
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          style={{ padding: "10px", fontSize: "16px" }}
        />
        <Button fullWidth auto type="submit" color="primary">
          Log In
        </Button>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <p>
          Not registered? <Link to="/register">Sign Up</Link>.
        </p>
      </form>
    </div>
  );
};

export default LoginPage;
