import React, { useState, useEffect } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Login from "./components/Login"; // Assuming you have this component set up based on the previous instructions
import PortfolioSummary from "./components/PortfolioSummary";
import StockList from "./components/StockList";
import StockDetails from "./components/StockDetails";
import AddStock from "./components/AddStock";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState("");
  const [stocks, setStocks] = useState([]);
  const [selectedStock, setSelectedStock] = useState(null);
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setIsLoggedIn(true);
      setToken(storedToken);
      fetchStocks(storedToken);
    }
  }, []);

  const onLoginSuccess = (token) => {
    localStorage.setItem("token", token);
    setIsLoggedIn(true);
    setToken(token);
    fetchStocks(token);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    setToken("");
    setStocks([]);
  };

  const fetchStocks = (token) => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/portfolio/testUser`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setStocks(response.data);
        setRefresh(false);
      })
      .catch((error) => console.error("Failed to fetch stocks", error));
  };

  const handleRefresh = () => {
    setRefresh(true);
    fetchStocks(token);
  };

  const handleStockSelect = (ticker) => {
    setSelectedStock(ticker);
  };

  // Check if user is not logged in to show the login form
  if (!isLoggedIn) {
    return <Login onLoginSuccess={onLoginSuccess} />;
  }

  return (
    <Container fluid>
      <Row className="justify-content-md-center">
        <h1>My Portfolio</h1>
        <button onClick={handleLogout}>Logout</button>
      </Row>
      <Row>
        <Col>
          <PortfolioSummary username="testUser" />
        </Col>
      </Row>
      <Row>
        <Col xs={4}>
          <StockList stocks={stocks} onSelect={handleStockSelect} />
          <AddStock username="testUser" onStockAdded={handleRefresh} />
        </Col>
        <Col xs={8}>
          {selectedStock ? (
            <StockDetails ticker={selectedStock} username="testUser" />
          ) : (
            <div>Select a stock to view details.</div>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
