import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [portfolio, setPortfolio] = useState({ totalValue: 0, symbols: [] });
  const [selectedSymbolData, setSelectedSymbolData] = useState(null);

  useEffect(() => {
    fetch("/portfolio_symbols")
      .then((response) => response.json())
      .then((data) => {
        setPortfolio({
          totalValue: data.total_portfolio_value,
          symbols: data.stocks,
        });
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleSymbolClick = (ticker) => {
    // Assuming you have an endpoint to fetch details for a specific symbol
    fetch(`/weekly_series/${ticker}`)
      .then((response) => response.json())
      .then((data) => setSelectedSymbolData(data))
      .catch((error) => console.error("Error:", error));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Data from Backend</h1>
        <p>Total Portfolio Value: {portfolio.totalValue}</p>
        <ul>
          {portfolio.symbols.map((symbol) => (
            <li
              key={symbol.ticker}
              onClick={() => handleSymbolClick(symbol.ticker)}
            >
              {symbol.ticker}
            </li>
          ))}
        </ul>
        {selectedSymbolData && (
          <div>
            <h2>Selected Symbol: {selectedSymbolData.symbol}</h2>
            <pre>{JSON.stringify(selectedSymbolData.data, null, 2)}</pre>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
