import React, { useState, useEffect } from "react";
import { useLocation, useParams } from "react-router-dom";
import {
  Table,
  TableHeader,
  TableColumn,
  TableBody,
  TableRow,
  TableCell,
} from "@nextui-org/react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

const Content = ({ marginTop = "20px" }) => {
  const location = useLocation();
  const [stockInfo, setStockInfo] = useState([]);
  const { ticker } = useParams();
  const isSummaryPage = location.pathname.includes("summary") || !ticker;

  useEffect(() => {
    if (ticker) {
      fetch(`https://mcsbt-integration-416413.lm.r.appspot.com/${ticker}`)
        .then((response) => response.json())
        .then((data) => {
          if (data && data.stock_info) {
            const formattedData = Object.entries(data.stock_info).map(
              ([date, info]) => ({
                date,
                open: parseFloat(info["1. open"]),
                high: parseFloat(info["2. high"]),
                low: parseFloat(info["3. low"]),
                close: parseFloat(info["4. close"]),
                volume: parseFloat(info["5. volume"]),
              })
            );
            formattedData.sort((a, b) => new Date(a.date) - new Date(b.date));
            setStockInfo(formattedData);
          } else {
            console.error("Missing or invalid stock_info data");
            setStockInfo([]);
          }
        })
        .catch((error) => {
          console.error("Error fetching stock information:", error);
          setStockInfo([]);
        });
    }
  }, [ticker]);

  const displayTicker = ticker ? ticker.toUpperCase() : "N/A";

  if (isSummaryPage) {
    return (
      <div style={{ marginTop, textAlign: "center" }}>
        <h2>Summary Information</h2>
        {/* Place summary content here */}
      </div>
    );
  }

  return (
    <div style={{ marginTop, width: "100%", maxWidth: "800px" }}>
      <h2
        style={{
          color: "white",
          margin: "0 0 20px",
          fontWeight: "500",
          fontSize: "20px",
        }}
      >
        {displayTicker} Weekly Time Series ($)
      </h2>
      <Table aria-label="Stock Information Table">
        {/* Table rows and columns remain unchanged */}
      </Table>
    </div>
  );
};

export default Content;
