// src/components/SymbolDetails.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const SymbolDetails = ({ symbol }) => {
  const [details, setDetails] = useState(null);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        const { data } = await axios.get(`/api/symbols/${symbol}`);
        setDetails(data);
      } catch (error) {
        console.error("Failed to fetch symbol details");
      }
    };

    if (symbol) {
      fetchDetails();
    }
  }, [symbol]);

  const handleUpdate = async () => {
    // Assume you have a quantity input or similar for updates
    try {
      await axios.put(`/api/symbols/${symbol}`, { quantity: details.quantity });
      alert("Quantity updated successfully");
    } catch (error) {
      alert("Failed to update quantity");
    }
  };

  if (!details) return <p>Select a symbol to see details</p>;

  return (
    <div className="symbol-details">
      <h2>
        {details.name} ({details.symbol})
      </h2>
      <p>
        Quantity:{" "}
        <input
          type="number"
          value={details.quantity}
          onChange={(e) => setDetails({ ...details, quantity: e.target.value })}
        />
      </p>
      <button onClick={handleUpdate}>Update Quantity</button>
    </div>
  );
};

export default SymbolDetails;
