// StockDetails.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const StockDetails = ({ ticker }) => {
  const [details, setDetails] = useState({ ticker: "", quantity: 0 });

  useEffect(() => {
    const fetchStockDetails = async () => {
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/portfolio/pante`
      );
      const stockDetails = response.data.find(
        (stock) => stock.ticker === ticker
      );
      if (stockDetails) {
        setDetails(stockDetails);
      }
    };

    fetchStockDetails();
  }, [ticker]);

  const handleQuantityChange = (e) => {
    setDetails((prevDetails) => ({ ...prevDetails, quantity: e.target.value }));
  };

  const updateQuantity = async () => {
    // Implement the API call to update the quantity
    console.log("Updating quantity to", details.quantity);
  };

  return (
    <div>
      <h3>Stock Details for {ticker}</h3>
      <Form>
        <Form.Group>
          <Form.Label>Quantity</Form.Label>
          <Form.Control
            type="number"
            value={details.quantity}
            onChange={handleQuantityChange}
          />
        </Form.Group>
        <Button variant="primary" onClick={updateQuantity}>
          Update Quantity
        </Button>
      </Form>
    </div>
  );
};
const removeStock = async (ticker) => {
  const token = localStorage.getItem("token");
  try {
    await axios.delete(
      `${process.env.REACT_APP_API_URL}/remove-stock/${username}/${ticker}`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    // Refresh stock list
  } catch (error) {
    console.error("Failed to remove stock", error);
  }
};

export default StockDetails;
