// PortfolioSummary.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const PortfolioSummary = ({ username }) => {
  const [portfolioValue, setPortfolioValue] = useState(0);

  useEffect(() => {
    const fetchPortfolioValue = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/portfolio-value/${username}`
        );
        setPortfolioValue(response.data.total_portfolio_value);
      } catch (error) {
        console.error("Failed to fetch portfolio value", error);
      }
    };

    fetchPortfolioValue();
  }, [username]);

  return (
    <div>
      <h2>General Summary</h2>
      <p>Total Portfolio Value: ${portfolioValue}</p>
    </div>
  );
};

export default PortfolioSummary;
