import React, { useState, useEffect, useRef } from "react";
import { useLocation, useParams } from "react-router-dom";
import {
  Table,
  TableHeader,
  TableColumn,
  TableBody,
  TableRow,
  TableCell,
} from "@nextui-org/react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const Content = ({ marginTop = "20px" }) => {
  const location = useLocation();
  const [stockInfo, setStockInfo] = useState([]);
  const { ticker } = useParams();

  const isSummaryPage =
    location.pathname.includes("summary") || typeof ticker === "undefined";

  useEffect(() => {
    if (ticker) {
      fetch(`https://mcsbt-integration-vr.ew.r.appspot.com/${ticker}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          if (data && data.stock_info) {
            let formattedData = Object.entries(data.stock_info).map(
              ([date, info]) => ({
                date,
                open: parseFloat(info["1. open"]),
                high: parseFloat(info["2. high"]),
                low: parseFloat(info["3. low"]),
                close: parseFloat(info["4. close"]),
                volume: parseInt(info["5. volume"], 10),
              })
            );
            formattedData.sort((a, b) => b.date.localeCompare(a.date));
            setStockInfo(formattedData);
          } else {
            console.error("Missing or invalid stock_info data");
            setStockInfo([]);
          }
        })
        .catch((error) => {
          console.error("Error fetching stock information:", error);
          setStockInfo([]);
        });
    }
  }, [ticker]); // Depend on ticker for re-fetching data

  const displayTicker = ticker ? ticker.toUpperCase() : "N/A";

  if (isSummaryPage) {
    return (
      <div
        style={{
          marginTop,
          width: "100%",
          maxWidth: "800px",
          textAlign: "center",
          color: "green", // Adjust the color for the summary page
          background: "purple", // Light background color for contrast
          padding: "20px",
          borderRadius: "8px",
          boxShadow: "0 4px 14px 0 rgba(0,0,0,0.1)",
        }}
      >
        <h1> LISTOCK</h1>
      </div>
    );
  }

  return (
    <div style={{ marginTop, width: "100%", maxWidth: "800px" }}>
      <h2
        style={{
          color: "purple",
          margin: "0 0 20px",
          fontWeight: "800",
          fontSize: "20px",
        }}
      >
        {displayTicker} ($)
      </h2>

      {/* ResponsiveContainer from Recharts to make the chart responsive */}
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={stockInfo}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line
            type="monotone"
            dataKey="open"
            stroke="#8884d8"
            activeDot={{ r: 8 }}
          />
          <Line type="monotone" dataKey="close" stroke="#82ca9d" />
        </LineChart>
      </ResponsiveContainer>

      {/* ...existing Table code remains unchanged */}
    </div>
  );
};

export default Content;
